<?php use_stylesheet('table.css') ?>
<table cellspacing="0">	<tr>		<th><?php echo __('Name') ?></th>		<th><?php echo __('Category') ?></th>		<th><?php echo __('Date Created') ?></th>		<th><?php echo __('No. of Users') ?></th>		<th><?php echo __('Activated') ?></th>		<th></th>	</tr>	<?php foreach( $networks as $network ): ?>	<tr>		<td><?php echo link_to($network->getTitle(), url_for( 'network_show', $network)) ?></td>		<td><?php echo $network->getNetworkCategory()->getTitle() ?></td>		<td><?php echo $network->getCreatedAt() ?></td>		<td><?php echo count($network->fetchUsersQuery()->fetchArray()) ?></td>		<td>		<?php if ($network->getIsActivated() == 1) : ?>			<img src="/images/ok.png">		<?php else : ?>			<img src="/images/delete.png">		<?php endif;?>		</td>		<td><?php echo link_to('Edit', url_for( 'network_show', $network), array( "class" => "sf_admin_action_edit")) ?></td>	</tr>	<?php endforeach; ?></table>