<?php use_stylesheet('message.css') ?>
<?php slot( 'title', $network->getTitle().' - Friends' )?>
<?php slot( 'pagetitle', '<h1>'.$network->getTitle().' - Friends </h1>' )?>
	<h3>Friends</h3>
    <ul id="messagenavigation">
	      <li><?php echo link_to('Search Friend', 'friend_searchfriend', $network) ?></li>	</ul>	<p>
	<?php if($friends):?>		<?php foreach ($friends as $friend) : ?>			<span><?php echo $friend['username'] ?></span>			<span><?php echo link_to('Follow', url_for('@friend_followfriend?username='.$friend['username']))?></span>			<span><?php echo link_to('Profile', url_for('@friend_showfriend?username='.$friend['username']))?></span>			<span><?php echo link_to('Block', url_for('@friend_blockfriend?username='.$friend['username']))?></span>			<br>		<?php endforeach;?>	<?php endif; ?>		</p>	<h3>Friend Requests</h3>	<?php if ($friendRequests) :?>	<p>		<?php foreach ($friendRequests as $friendRequest) : ?>			<span><?php echo $friendRequest['username'] ?></span>			<br></br>			<span><?php echo link_to('Accept', url_for('@friend_acceptfriendrequest?connection='.$friendRequest['connection_id']))?></span>			<span><?php echo link_to('View', url_for('@friend_showfriend?username='.$friendRequest['username']))?></span>			<span><?php echo link_to('Block', url_for('@friend_blockfriend?username='.$friendRequest['username']))?></span>		<?php endforeach;?>	</p>	<?php else:?>	<p>		<span>No new friend requests</span>	</p>	<?php endif; ?>		<h3>Latest users...</h3>	<?php if ($friendsList) :?>	<p>		<?php foreach ($friendsList as $friend) : ?>			<?php if ($friend['sfGuardUser']['id'] != $sf_user->getUserId()): ?>				<span>					<img style="width:25px;" src="http://<?php echo $_SERVER['SERVER_NAME'] ?><?php echo $sf_request->getRelativeUrlRoot() ?>/uploads/profilepics/50x50/<?php echo $friend['sfGuardUser']['UserProfile'][0]['profile_pic'] ?>">				</span>				<span><?php echo $friend['sfGuardUser']['username'] ?></span>				<span><?php echo link_to('Send Message', url_for('@message_send?username='.$friend['sfGuardUser']['username']))?></span>		 		<span><?php echo link_to('Add as Friend', url_for('@friend_addfriendrequest?username='.$friend['sfGuardUser']['username']))?></span>		 		<span><?php echo link_to('View', url_for('@friend_showfriend?username='.$friend['sfGuardUser']['username']))?></span>				<br>			<?php endif; ?>		<?php endforeach;?>	</p>	<?php endif; ?>