<?php use_stylesheet('message.css') ?>
<?php slot( 'title', $network->getTitle().' - Friends' )?>
<?php slot( 'pagetitle', '<h1>'.$network->getTitle().' - Friends </h1>' )?>
	<h3>Friends</h3>
    <ul id="messagenavigation">
	      <li><?php echo link_to('Find Users', 'friend_searchfriend', $network) ?></li>	</ul>	<?php if($friends):?>		<ul class="friends_list">		<?php foreach ($friends as $friend) : ?>			<li>			<?php echo $friend['username'] ?>			<?php echo link_to('Follow', url_for('@friend_followfriend?username='.$friend['username']))?>			<?php echo link_to('Profile', url_for('@friend_showfriend?username='.$friend['username']))?>			<?php echo link_to('Block', url_for('@friend_blockfriend?username='.$friend['username']))?>			</li>		<?php endforeach;?>		</ul>	<?php endif; ?>		<h3>Friend Requests</h3>	<?php if ($friendRequests) :?>	<ul class="request_list">		<?php foreach ($friendRequests as $friendRequest) : ?>			<li>			<?php echo $friendRequest['username'] ?>			<?php echo link_to('Accept', url_for('@friend_acceptfriendrequest?connection='.$friendRequest['connection_id']))?>			<?php echo link_to('View', url_for('@friend_showfriend?username='.$friendRequest['username']))?>			<?php echo link_to('Block', url_for('@friend_blockfriend?username='.$friendRequest['username']))?>			</li>		<?php endforeach;?>	</ul>	<?php else:?>	<p>		<span>No new friend requests</span>	</p>	<?php endif; ?>		<h3>Latest users...</h3>	<?php if ($friendsList) :?>	<ul class="latestuser_list">		<?php foreach ($friendsList as $friend) : ?>			<?php if ($friend['sfGuardUser']['id'] != $sf_user->getUserId()): ?>			<li>				<span style="float:left;padding-right:3px;">					<img style="width:25px;" src="http://<?php echo $_SERVER['SERVER_NAME'] ?><?php echo $sf_request->getRelativeUrlRoot() ?>/uploads/profilepics/50x50/<?php echo $friend['sfGuardUser']['UserProfile'][0]['profile_pic'] ?>">				</span>				<span><?php echo $friend['sfGuardUser']['username'] ?></span>				<br>				<span><?php echo link_to('Send Message', url_for('@message_send?username='.$friend['sfGuardUser']['username']))?></span>		 		<span><?php echo link_to('Add as Friend', url_for('@friend_addfriendrequest?username='.$friend['sfGuardUser']['username']))?></span>		 		<span><?php echo link_to('View', url_for('@friend_showfriend?username='.$friend['sfGuardUser']['username']))?></span>			</li>			<?php endif; ?>		<?php endforeach;?>	</ul>	<?php endif; ?>