<?php use_stylesheet('speakout.css') ?><?php slot( 'pagetitle', '<h1>'.$network->getTitle().' - Speakout </h1>' )?>	<h3>Speakout: Home</h3>    <ul id="speakoutnavigation">	      <li><?php echo link_to('Create Topic', 'speakout_addtopic', $network) ?></li>	      <li><?php echo link_to('New Replies', 'speakout_addtopic', $network) ?></li>	</ul>	<p class="category">	<?php if (count($categorys) > 0) :?>		<?php foreach ( $categorys as $category ) : ?>			<h5><?php echo 'Category->'.$category['title']?></h5>			<span class="categorydescription"><?php echo $category['description']?></span>			<ul class="topiclist">			<?php foreach ( $category['SpeakoutTopic'] as $topic ) : ?>				<li class="topiclist"><?php echo link_to(  $topic['title'], url_for( '@speakout_showtopic?topic='.$topic['id'].'&network_id='.$network->getId() ) ) ?></li>			<?php endforeach; ?>			</ul>		<?php endforeach; ?>	</p>	<?php else : ?>		<?php echo 'There are no categories' ?>	<?php endif;?>